(function(){const mermaidEles=document.querySelectorAll(".mermaid");const mermaidSlideEle=document.querySelector("#remark-wrap");const mermaidContentEle=mermaidSlideEle.querySelector("#remark-content");const mermaidSlideOutBtn=mermaidSlideEle.querySelector("#remark-out");let mermaidSlideStatus=false;mermaid.mermaidAPI.initialize({theme:'forest',startOnLoad:true,securityLevel:'loose',flowchart:{}});mermaidEles.forEach(m=>{let obverser=new MutationObserver(function(mutationList){if(m.firstElementChild){obverser.disconnect();m.style.visibility='unset';m.nextElementSibling.classList.add("hidden");m.nextElementSibling.nextElementSibling.classList.add("hidden");m.querySelectorAll(".clickable").forEach(e=>{e.addEventListener("click",callNotes);})}});obverser.observe(m,{childList:true});});window.callNotes=function(){let nodeid=`#remark-${this.getAttribute("title")}`;if(!mermaidSlideStatus){mermaidSlideEle.classList.add("slideIn");mermaidSlideEle.classList.remove("slideOut");}
mermaidContentEle.innerHTML=document.querySelector(nodeid)?document.querySelector(nodeid).innerHTML:"";}
mermaidSlideOutBtn.addEventListener("click",function(){mermaidSlideEle.classList.add("slideOut");mermaidSlideEle.classList.remove("slideIn");mermaidSlideStatus=false;})})();