const mermaidEles=document.querySelectorAll(".mermaid");const mermaidSlideEle=document.querySelector("#remark-wrap");const mermaidContentEle=mermaidSlideEle.querySelector("#remark-content");const mermaidSlideOutBtn=mermaidSlideEle.querySelector("#remark-out");let mermaidSlideStatus=false;mermaid.mermaidAPI.initialize({theme:'forest',startOnLoad:true,securityLevel:'loose',flowchart:{}});setTimeout(function(){mermaidEles.forEach(m=>{let clickEles=m.querySelectorAll(".clickable");clickEles.forEach(e=>{e.addEventListener("click",callNotes);})});},5000);function callNotes(){let nodeid=`#remark-${this.getAttribute("title")}`;if(!mermaidSlideStatus){mermaidSlideEle.classList.add("slideIn");mermaidSlideEle.classList.remove("slideOut");}
mermaidContentEle.innerHTML=document.querySelector(nodeid)?document.querySelector(nodeid).innerHTML:"";}
mermaidSlideOutBtn.addEventListener("click",function(){mermaidSlideEle.classList.add("slideOut");mermaidSlideEle.classList.remove("slideIn");mermaidSlideStatus=false;})